sp_widget.client_script:
api.controller=function($rootScope, $scope) {
  /* widget controller */
  var c = this;
	$rootScope.$on("onNoteSelection", function(event,data){
		c.data.noteID = data;
		c.server.get({
			action: 'getNoteDetails',
			noteID: c.data.noteID	
		}).then(function(res){
			c.data.title = res.data.note.title;
			c.data.note = res.data.note.note;
			c.data.number = res.data.note.number;

		})

	});
	c.updateNote = function(op) {
		c.server.get({
			action: 'updateNote',
			noteTitle: c.data.title,
			noteBody: c.data.note,
			noteID: c.data.noteID,
		}).then(function(res){
			alert("on way");
			alert("updating");
			$rootScope.$emit('OnUpdateNote', c.data.noteID);

		})
	}

};

sp_widget.script:
(function() {

	if(input && input.noteID){
		var note = new GlideRecord("x_snc_createnotes_note");
		if(input.action == "getNoteDetails"){
			if (note.get(input.noteID)){
				data.note = {};
				$sp.getRecordValues(data.note, note, "title, note, sys_id, number");
			}
		}
		else if(input.action == "updateNote"){
			if (note.get(input.noteID)){
				note.title = input.noteTitle;
				note.note = input.noteBody;
				note.update();
			}
		}
	}

})();
