sp_widget.client_script:
api.controller=function($rootScope, $scope) {
  /* widget controller */
  var c = this;
	c.onSelection = function(index){
		var id  = c.data.noteList[index].sys_id;
		console.log("list-",id);
		$rootScope.$emit("onNoteSelection", id);

	}
	$rootScope.$on("OnUpdateNote", function(event,data){
		c.data.noteID = data;
		alert("Update");
		c.server.refresh();

	});

};

sp_widget.script:
(function() {
	data.noteList = [];
  var gr = new GlideRecord('x_snc_createnotes_note');
	gr.addQuery('user', gs.getUser().getID());
	gr.orderByDesc('sys_created_on');
	gr.query();
	while (gr.next()) {
		var obj = {};
		$sp.getRecordDisplayValues(obj, gr, 'number,title,sys_id');
    obj.note = gr.getValue('note').slice(0,20);
		data.noteList.push(obj);
	}

	if (input && input.action){
		if (input.action == "getNoteDetails") {
			var noteRecord = new GlideRecord("x_snc_createnotes_note");

		}
	}
})();
